values (#{alarmKey,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{alarmLevelCode,jdbcType=VARCHAR}, 
  #{alarmEmailManageId,jdbcType=VARCHAR},
  #{firstTimeSec,jdbcType=VARCHAR}, #{frequencyTimeSec,jdbcType=VARCHAR}, #{handleClass,jdbcType=VARCHAR}, 
  #{classParamter,jdbcType=VARCHAR}, #{deleteFlag,jdbcType=INTEGER}, #{remark,jdbcType=VARCHAR}
