mapper.select:
select 

from proxypaymessage
where 1=1

    and GLOBALMESSAGEID = #{globalmessageid,jdbcType=VARCHAR}


    and PHONENUMBER = #{phonenumber,jdbcType=VARCHAR}


    and CPID = #{cpid,jdbcType=VARCHAR}


    and SERVICEID = #{serviceid,jdbcType=VARCHAR}


    and SERVICENAME = #{servicename,jdbcType=VARCHAR}


    and FEETYPE = #{feetype,jdbcType=VARCHAR}


    and FEE = #{fee,jdbcType=VARCHAR}


    and ACCOUNTID = #{accountid,jdbcType=VARCHAR}


    and STATUS = #{status,jdbcType=VARCHAR}


    and VALIDATECODE = #{validatecode,jdbcType=VARCHAR}


    and USERTYPE = #{usertype,jdbcType=VARCHAR}


    and SPID = #{spid,jdbcType=VARCHAR}


    and PERORID = #{perorid,jdbcType=VARCHAR}


    and PRODUCTID = #{productid,jdbcType=VARCHAR}


    and CHARGEPARTY = #{chargeparty,jdbcType=VARCHAR}


    and CREATEDATE = #{createdate,jdbcType=VARCHAR}


    and UPDATEDATE = #{updatedate,jdbcType=VARCHAR}


    and EXTENDFILED1 = #{extendfiled1,jdbcType=VARCHAR}


    and EXTENDFIILED2 = #{extendfiiled2,jdbcType=VARCHAR}


    and PRODUCTNAME = #{productname,jdbcType=VARCHAR}


    and SOURCE_TYPE = #{sourceType,jdbcType=VARCHAR}

