mapper.select:

   select 

from user_service_send
    where 1=1

    and user_service_send_id = #{userServiceSendId,jdbcType=VARCHAR}


    and phone_number = #{phoneNumber,jdbcType=VARCHAR}


    and product_info_id = #{productInfoId,jdbcType=VARCHAR}


    and request_id = #{requestId,jdbcType=VARCHAR}


    and charge_phone_number = #{chargePhoneNumber,jdbcType=VARCHAR}


    and content_info_id = #{contentInfoId,jdbcType=VARCHAR}


    and status = #{status,jdbcType=INTEGER}


    and service_type = #{serviceType,jdbcType=INTEGER}


    and send_service_type = #{sendServiceType,jdbcType=INTEGER}


    and send_service_rel_id = #{sendServiceRelId,jdbcType=VARCHAR}


    and error_info = #{errorInfo,jdbcType=VARCHAR}


    and send_type = #{sendType,jdbcType=INTEGER}


    and mms_path = #{mmsPath,jdbcType=VARCHAR}


    and batch_id = #{batchId,jdbcType=VARCHAR}


       and DATE_FORMAT(report_time, '%Y-%m-%d %H:%i:%S')>=  DATE_FORMAT(#{reportTimeStart}, '%Y-%m-%d %H:%i:%S')     


      and DATE_FORMAT(report_time, '%Y-%m-%d %H:%i:%S')<=  DATE_FORMAT(#{reportTimeEnd}, '%Y-%m-%d %H:%i:%S')     

  order by send_time desc


 select 
count(1)
from user_service_send
  where 1=1

    and user_service_send_id = #{userServiceSendId,jdbcType=VARCHAR}


