    and mms_path = #{mmsPath,jdbcType=VARCHAR}


    and batch_id = #{batchId,jdbcType=VARCHAR}


       and DATE_FORMAT(report_time, '%Y-%m-%d %H:%i:%S')>=  DATE_FORMAT(#{reportTimeStart}, '%Y-%m-%d %H:%i:%S')     


