mapper.select:

    select 

    from content_info where 1=1

	        and product_info_id = #{productInfoId,jdbcType=VARCHAR}


	        and cp_info_id = #{cpInfoId,jdbcType=VARCHAR}


	        and content_code = #{contentCode,jdbcType=VARCHAR}


	        and content_name = #{contentName,jdbcType=VARCHAR}


	        and content_remark = #{contentRemark,jdbcType=VARCHAR}


	        and author = #{author,jdbcType=VARCHAR}


        and status = #{status,jdbcType=INTEGER}


	        and content_path = #{contentPath,jdbcType=VARCHAR}


        and content_type = #{contentType,jdbcType=INTEGER}


	        and key_word = #{keyWord,jdbcType=VARCHAR}


        and is_realtime = #{isRealtime,jdbcType=INTEGER}


	        and realtime_content_url = #{realtimeContentUrl,jdbcType=VARCHAR}


	        and reject_reason = #{rejectReason,jdbcType=VARCHAR}


        and is_apply_delete = #{isApplyDelete,jdbcType=INTEGER}


	        and web_pic = #{webPic,jdbcType=VARCHAR}


           and DATE_FORMAT(start_time, '%Y-%m-%d %H:%i:%S')>=  DATE_FORMAT(#{startTimeStart}, '%Y-%m-%d %H:%i:%S')     


          and DATE_FORMAT(start_time, '%Y-%m-%d %H:%i:%S')<=  DATE_FORMAT(#{startTimeEnd}, '%Y-%m-%d %H:%i:%S')     


           and DATE_FORMAT(end_time, '%Y-%m-%d %H:%i:%S')>=  DATE_FORMAT(#{endTimeStart}, '%Y-%m-%d %H:%i:%S')     


          and DATE_FORMAT(end_time, '%Y-%m-%d %H:%i:%S')<=  DATE_FORMAT(#{endTimeEnd}, '%Y-%m-%d %H:%i:%S')     


	        and singer = #{productInfoId,jdbcType=VARCHAR}


	        and feature_str = #{featureStr,jdbcType=VARCHAR}


	        and aduit_user = #{aduitUser,jdbcType=VARCHAR}


	        and version_id = #{versionId,jdbcType=VARCHAR}


        and is_send = #{isSend,jdbcType=INTEGER}


	        and set_send_time_user = #{setSendTimeUser,jdbcType=VARCHAR}


	        and ad_send_id = #{adSendId,jdbcType=VARCHAR}

    select 
    count(1)
    from content_info where 1=1

	        and product_info_id = #{productInfoId,jdbcType=VARCHAR}


	        and cp_info_id = #{cpInfoId,jdbcType=VARCHAR}


	        and content_code = #{contentCode,jdbcType=VARCHAR}


	        and content_name = #{contentName,jdbcType=VARCHAR}


	        and content_remark = #{contentRemark,jdbcType=VARCHAR}


	        and author = #{author,jdbcType=VARCHAR}


        and status = #{status,jdbcType=INTEGER}


	        and content_path = #{contentPath,jdbcType=VARCHAR}


        and content_type = #{contentType,jdbcType=INTEGER}


	        and key_word = #{keyWord,jdbcType=VARCHAR}


        and is_realtime = #{isRealtime,jdbcType=INTEGER}


	        and realtime_content_url = #{realtimeContentUrl,jdbcType=VARCHAR}


	        and reject_reason = #{rejectReason,jdbcType=VARCHAR}


        and is_apply_delete = #{isApplyDelete,jdbcType=INTEGER}


	        and web_pic = #{webPic,jdbcType=VARCHAR}


           and DATE_FORMAT(start_time, '%Y-%m-%d %H:%i:%S')>=  DATE_FORMAT(#{startTimeStart}, '%Y-%m-%d %H:%i:%S')     


          and DATE_FORMAT(start_time, '%Y-%m-%d %H:%i:%S')<=  DATE_FORMAT(#{startTimeEnd}, '%Y-%m-%d %H:%i:%S')     


           and DATE_FORMAT(end_time, '%Y-%m-%d %H:%i:%S')>=  DATE_FORMAT(#{endTimeStart}, '%Y-%m-%d %H:%i:%S')     


          and DATE_FORMAT(end_time, '%Y-%m-%d %H:%i:%S')<=  DATE_FORMAT(#{endTimeEnd}, '%Y-%m-%d %H:%i:%S')     


           and DATE_FORMAT(create_time, '%Y-%m-%d %H:%i:%S')>=  DATE_FORMAT(#{createTimeStart}, '%Y-%m-%d %H:%i:%S')     


          and DATE_FORMAT(create_time, '%Y-%m-%d %H:%i:%S')<=  DATE_FORMAT(#{createTimeEnd}, '%Y-%m-%d %H:%i:%S')     


	        and singer = #{productInfoId,jdbcType=VARCHAR}


	        and feature_str = #{featureStr,jdbcType=VARCHAR}


	        and aduit_user = #{aduitUser,jdbcType=VARCHAR}


	        and version_id = #{versionId,jdbcType=VARCHAR}


        and is_send = #{isSend,jdbcType=INTEGER}


	        and set_send_time_user = #{setSendTimeUser,jdbcType=VARCHAR}


	        and ad_send_id = #{adSendId,jdbcType=VARCHAR}
