mapper.select:

select 

from st_succ_ratio
where st_time = #{stTime,jdbcType=VARCHAR}
  and province = #{province,jdbcType=VARCHAR}
  and product_info_id = #{productInfoId,jdbcType=VARCHAR}
  and sp_info_id = #{spInfoId,jdbcType=VARCHAR}

mapper.delete:

delete from st_succ_ratio
where st_time = #{stTime,jdbcType=VARCHAR}
  and province = #{province,jdbcType=VARCHAR}
  and product_info_id = #{productInfoId,jdbcType=VARCHAR}
  and sp_info_id = #{spInfoId,jdbcType=VARCHAR}

mapper.update:

update st_succ_ratio


    recv_sms_num = #{recvSmsNum,jdbcType=DOUBLE},


    send_sms_num = #{sendSmsNum,jdbcType=DOUBLE},


    sms_gateway_num = #{smsGatewayNum,jdbcType=DOUBLE},


    sms_user_num = #{smsUserNum,jdbcType=DOUBLE},


    send_mms_num = #{sendMmsNum,jdbcType=DOUBLE},


    mms_gateway_num = #{mmsGatewayNum,jdbcType=DOUBLE},


    mms_user_num = #{mmsUserNum,jdbcType=DOUBLE},


where st_time = #{stTime,jdbcType=VARCHAR}
  and province = #{province,jdbcType=VARCHAR}
  and product_info_id = #{productInfoId,jdbcType=VARCHAR}
  and sp_info_id = #{spInfoId,jdbcType=VARCHAR}


update st_succ_ratio
set recv_sms_num = #{recvSmsNum,jdbcType=DOUBLE},
  send_sms_num = #{sendSmsNum,jdbcType=DOUBLE},
  sms_gateway_num = #{smsGatewayNum,jdbcType=DOUBLE},
  sms_user_num = #{smsUserNum,jdbcType=DOUBLE},
  send_mms_num = #{sendMmsNum,jdbcType=DOUBLE},
  mms_gateway_num = #{mmsGatewayNum,jdbcType=DOUBLE},
  mms_user_num = #{mmsUserNum,jdbcType=DOUBLE}
where st_time = #{stTime,jdbcType=VARCHAR}
  and province = #{province,jdbcType=VARCHAR}
  and product_info_id = #{productInfoId,jdbcType=VARCHAR}
  and sp_info_id = #{spInfoId,jdbcType=VARCHAR}

mapper.select:


   select 

from st_succ_ratio
    where 1=1

    and province = #{province,jdbcType=VARCHAR}


    and product_info_id = #{productInfoId,jdbcType=VARCHAR}


    and sp_info_id = #{spInfoId,jdbcType=VARCHAR}


       and DATE_FORMAT(st_time, '%Y-%m-%d %H:%i:%S')>=  DATE_FORMAT(#{stTimeStart}, '%Y-%m-%d %H:%i:%S')     


      and DATE_FORMAT(st_time, '%Y-%m-%d %H:%i:%S')<=  DATE_FORMAT(#{stTimeEnd}, '%Y-%m-%d %H:%i:%S')


 select 
count(1)
from st_succ_ratio
  where 1=1

    and province = #{province,jdbcType=VARCHAR}


    and product_info_id = #{productInfoId,jdbcType=VARCHAR}


    and sp_info_id = #{spInfoId,jdbcType=VARCHAR}


       and DATE_FORMAT(st_time, '%Y-%m-%d %H:%i:%S')>=  DATE_FORMAT(#{stTimeStart}, '%Y-%m-%d %H:%i:%S')     


      and DATE_FORMAT(st_time, '%Y-%m-%d %H:%i:%S')<=  DATE_FORMAT(#{stTimeEnd}, '%Y-%m-%d %H:%i:%S')
